void ST7796_InitPins(void)
{
	CLOCK_EnableClock(kCLOCK_Port1);
	CLOCK_EnableClock(kCLOCK_Gpio1);
    CLOCK_EnableClock(kCLOCK_Port2);
    CLOCK_EnableClock(kCLOCK_Gpio2);


    /* P2_2: LCD_DC (GPIO Output) */
    const port_pin_config_t dc_config = {
        kPORT_PullDisable, kPORT_LowPullResistor, kPORT_FastSlewRate,
        kPORT_PassiveFilterDisable, kPORT_OpenDrainDisable, kPORT_LowDriveStrength,
        kPORT_MuxAlt0, /* GPIO */
        kPORT_InputBufferEnable, kPORT_InputNormal, kPORT_UnlockRegister
    };
    PORT_SetPinConfig(PORT2, 2U, &dc_config);

    /* P2_6: LCD_CS (GPIO Output) */
    PORT_SetPinConfig(PORT2, 6U, &dc_config);

    /* P2_7: LCD_RST (GPIO Output) */
    PORT_SetPinConfig(PORT2, 7U, &dc_config);

    /* P1_22: BL (GPIO Output) */
	PORT_SetPinConfig(PORT1, 22U, &dc_config);

    /* P2_3: LPSPI9_SCK (Alt 2) */
    const port_pin_config_t spi_config = {
        kPORT_PullUp, kPORT_LowPullResistor, kPORT_FastSlewRate,
        kPORT_PassiveFilterDisable, kPORT_OpenDrainDisable, kPORT_LowDriveStrength,
        kPORT_MuxAlt2, /* Flexcomm 9 */
        kPORT_InputBufferEnable, kPORT_InputNormal, kPORT_UnlockRegister
    };
    PORT_SetPinConfig(PORT2, 3U, &spi_config);

    /* P2_5: LPSPI9_SOUT/MOSI (Alt 2) */
    PORT_SetPinConfig(PORT2, 5U, &spi_config);

    /* P2_4: LPSPI9_SIN/MISO (Alt 2) - Optional if only writing */
    PORT_SetPinConfig(PORT2, 4U, &spi_config);
}

